import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Alert
} from "reactstrap";

import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { registerSchema } from "./UserValidations";

const Register = () => {
  const [msg, setMsg] = useState({ text: "", type: "" });
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();

    const { register, handleSubmit, formState: { errors } } = useForm({
  resolver: yupResolver(registerSchema)
});

const onSubmit = data => {
  console.log(data);  
};

    const fullName = e.target.fullName.value;
    const username = e.target.username.value;
    const password = e.target.password.value;
    const confirmPassword = e.target.confirmPassword.value;

    if (password !== confirmPassword) {
      setMsg({ text: "Passwords do not match!", type: "danger" });
      return;
    }

    try {
      const res = await axios.post("http://localhost:3001/register", {
        fullName,
        username,
        password,
      });

      if (res.data.success) {
        setMsg({ text: "Account created successfully!", type: "success" });

        setTimeout(() => navigate("/login"), 1000);
      } else {
        setMsg({ text: res.data.message, type: "danger" });
      }
    } catch {
      setMsg({ text: "Server error", type: "danger" });
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <Row className="w-100 justify-content-center">
        <Col md="5">
          <Card className="shadow-lg p-3">
            <CardBody>
              <h3 className="text-center mb-4">Create Account</h3>

              {msg.text && <Alert color={msg.type}>{msg.text}</Alert>}

              <Form onSubmit={handleRegister}>
                <FormGroup>
                  <Label>Full Name</Label>
                  <Input name="fullName" type="text" required />
                </FormGroup>

                <FormGroup>
                  <Label>Username</Label>
                  <Input name="username" type="text" required />
                </FormGroup>

                <FormGroup>
                  <Label>Password</Label>
                  <Input name="password" type="password" required />
                </FormGroup>

                <FormGroup>
                  <Label>Confirm Password</Label>
                  <Input name="confirmPassword" type="password" required />
                </FormGroup>

                <Button color="success" className="w-100 mt-3">
                  Register
                </Button>

                <div className="text-center mt-3">
                  <span>Already have an account? </span>
                  <Link to="/login">Login</Link>
                </div>
              </Form>

            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
