import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Input, Spinner } from "reactstrap";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [ratings, setRatings] = useState({});
  const [feedbacks, setFeedbacks] = useState({});
  const [loading, setLoading] = useState(false);

  const username = localStorage.getItem("username");

  // Fetch borrowed books
  const fetchBorrowedBooks = async () => {
    if (!username) return;
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:3001/borrowedbooks/${username}`);
      setBorrowedBooks(res.data);

      // Initialize ratings and feedbacks
      const initialRatings = {};
      const initialFeedbacks = {};
      res.data.forEach(b => {
        initialRatings[b.book?._id] = b.rating || 0;
        initialFeedbacks[b.book?._id] = b.feedback || "";
      });
      setRatings(initialRatings);
      setFeedbacks(initialFeedbacks);

    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const handleStarClick = (bookId, star, isReturned) => {
    if (!isReturned) {
      setRatings(prev => ({ ...prev, [bookId]: star }));
    }
  };

  const handleFeedbackChange = (bookId, value, isReturned) => {
    if (!isReturned) {
      setFeedbacks(prev => ({ ...prev, [bookId]: value }));
    }
  };

  // Return book + update rating in Book collection
  const handleReturn = async (borrowedId, bookId) => {
    const rating = ratings[bookId] || 0;
    const feedback = feedbacks[bookId] || "";

    try {
      // 1️⃣ Update BorrowedBook
      await axios.put(`http://localhost:3001/borrowedbooks/return/${borrowedId}`, {
        rating,
        feedback
      });

      // 2️⃣ Update Book rating collection
      if (rating > 0) {
        await axios.post(`http://localhost:3001/books/rate`, {
          username,
          bookId,
          rating
        });
      }

      // Remove from local list
      setBorrowedBooks(prev => prev.filter(b => b._id !== borrowedId));
      alert("Book returned and rated successfully!");

    } catch (err) {
      console.error(err);
      alert("Error returning or rating book");
    }
  };

  return (
    <Container className="py-4">
      <h3 className="text-center mb-4">My Borrowed Books</h3>

      {loading ? (
        <div className="text-center"><Spinner color="primary" /></div>
      ) : borrowedBooks.length === 0 ? (
        <p className="text-center">You have no borrowed books.</p>
      ) : (
        <Row>
          {borrowedBooks.map(b => {
            const isReturned = !!b.returnedAt;
            const book = b.book; // populated book
            return (
              <Col md="4" className="mb-3" key={b._id}>
                <Card className="shadow-sm">
                  <CardBody>
                    <h5>{book ? book.title : "Book details not found"}</h5>
                    <p>Author: {book?.author || "N/A"}</p>
                    <p>Year: {book?.year || "N/A"}</p>
                    <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                    {isReturned && <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>}

                    {/* Star Rating */}
                    <div className="mb-2">
                      {[1,2,3,4,5].map(star => (
                        <span
                          key={star}
                          style={{
                            cursor: isReturned ? "not-allowed" : "pointer",
                            color: star <= (ratings[book?._id] || 0) ? "gold" : "gray",
                            fontSize: "1.2rem",
                            marginRight: "3px"
                          }}
                          onClick={() => handleStarClick(book?._id, star, isReturned)}
                        >
                          ★
                        </span>
                      ))}
                    </div>

                    {/* Feedback */}
                    <Input
                      type="textarea"
                      placeholder="Leave feedback"
                      value={feedbacks[book?._id] || ""}
                      onChange={(e) => handleFeedbackChange(book?._id, e.target.value, isReturned)}
                      className="mb-2"
                      disabled={isReturned}
                    />

                    {/* Return button */}
                    {!isReturned && (
                      <Button color="success" className="w-100" onClick={() => handleReturn(b._id, book?._id)}>
                        Return & Rate
                      </Button>
                    )}
                  </CardBody>
                </Card>
              </Col>
            );
          })}
        </Row>
      )}
    </Container>
  );
};

export default MyBorrowedBooks;
