import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Table, Input } from "reactstrap";

const ViewBooks = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredBooks, setFilteredBooks] = useState([]);

  // جلب الكتب من الـ backend
  const getBooks = async () => {
    try {
      const res = await axios.get("http://localhost:3001/books"); // كل الكتب
      setBooks(res.data);
      setFilteredBooks(res.data);
    } catch (err) {
      console.log("Error fetching books:", err);
    }
  };

  useEffect(() => {
    getBooks();
  }, []);

  // بحث مباشر حسب العنوان
  useEffect(() => {
    const filtered = books.filter((b) =>
      b.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, [searchTerm, books]);

  return (
    <Container className="py-4">
      <Row>
        <Col md="12">
          <Card className="shadow p-3">
            <CardBody>
              <h3 className="mb-3 text-center">Available Books</h3>

              {/* حقل البحث */}
              <div className="mb-3">
                <Input
                  type="text"
                  placeholder="Search by Title"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              {/* جدول الكتب */}
              <Table bordered hover responsive>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Author</th>
                    <th>Year</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredBooks.length > 0 ? (
                    filteredBooks.map((b, index) => (
                      <tr key={b._id}>
                        <td>{index + 1}</td>
                        <td>{b.title}</td>
                        <td>{b.author}</td>
                        <td>{b.year}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="4" className="text-center">
                        No books found
                      </td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ViewBooks;
