import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Container, Row } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";

// === Components (components) ===
import Header from "./Components/Header";
import Footer from "./Components/Footer";
import Home from "./Components/Home";
import Login from "./Components/Login";
import Register from "./Components/Register";
import StudentDashboard from "./Components/StudentDashboard";
import ViewBooks from "./Components/ViewBooks";
import MyBorrowed from "./Components/MyBorrowed";
import AdminDashboard from "./Components/AdminDashboard";
import AddEditBook from "./Components/AddEditBook";
import BorrowedList from "./Components/BorrowedList";


const App = () => {
  return (
    <Container fluid>
      <Router>

        {/* Header Fixed */}
        <Row>
           <Header />
        </Row>

        {/* Main Content */}
        <Row className="main">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Student */}
            <Route path="/student" element={<StudentDashboard />} />
            <Route path="/view-books" element={<ViewBooks />} />
            <Route path="/my-borrowed" element={<MyBorrowed />} />

            {/* Admin */}
            <Route path="/admin" element={<AdminDashboard />} />
            <Route path="/add-book" element={<AddEditBook />} />
            <Route path="/borrowed-list" element={<BorrowedList />} />
          </Routes>
        </Row>

        {/* Footer */}
        <Row>
          <Footer />
        </Row>

      </Router>
    </Container>
  );
};

export default App;
