import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card, CardBody } from "reactstrap";
import axios from "axios";

const Dashboard = () => {
  const user = useSelector((state) => state.user.user);
  const [borrowedCount, setBorrowedCount] = useState(0);
  const [availableCount, setAvailableCount] = useState(0);

  useEffect(() => {
    if (user?.username) {
      axios.get(`http://localhost:3001/borrowedbooks/${user.username}`)
        .then(res => setBorrowedCount(res.data.length))
        .catch(err => console.log(err));

      axios.get(`http://localhost:3001/availablebooks`)
        .then(res => setAvailableCount(res.data.length))
        .catch(err => console.log(err));
    }
  }, [user]);

  return (
    <Container className="py-4">
      <Row>
        <Col md={4} className="text-center">
          {/* User image */}
          <img
            src={student}
            alt="user"
            style={{
              width: "140px",
              borderRadius: "50%",
              marginBottom: "15px",
              boxShadow: "0 4px 8px rgba(0,0,0,0.2)"
            }}
          />
          {/* User name */}
          <h3 style={{ marginBottom: "5px" }}>{user?.fullName || "Guest"}</h3>
          {/* Welcome message */}
          <p style={{ fontStyle: "italic", color: "#555" }}>
            Welcome, {user?.fullName || "Guest"}!
          </p>
          {/* Location component */}
          <Location />
        </Col>

        <Col md={8}>
          <Row className="g-3">
            <Col md={6}>
              <Card
                style={{
                  backgroundColor: "#f8d7da",
                  color: "#721c24",
                  borderRadius: "12px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.15)",
                  padding: "20px",
                  textAlign: "center"
                }}
              >
                <h5>Borrowed Books</h5>
                <h2>{borrowedCount}</h2>
              </Card>
            </Col>
            <Col md={6}>
              <Card
                style={{
                  backgroundColor: "#d4edda",
                  color: "#155724",
                  borderRadius: "12px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.15)",
                  padding: "20px",
                  textAlign: "center"
                }}
              >
                <h5>Available Books</h5>
                <h2>{availableCount}</h2>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
