import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchBorrowedBooks } from "../Features/BorrowedBookSlice";

const BorrowedList = () => {
  const dispatch = useDispatch();

  const { user } = useSelector((state) => state.user); 
  const { borrowedBooks } = useSelector((state) => state.borrowed);

  useEffect(() => {
    if (user?.username) {
      dispatch(fetchBorrowedBooks(user.username));
    }
  }, [dispatch, user]);

  console.log("ðŸ“š BorrowedBooks Data:", borrowedBooks);

  return (
    <div className="container mt-4">
      <h2>My Borrowed Books</h2>

      {borrowedBooks.length === 0 ? (
        <p>No borrowed books found.</p>
      ) : (
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Title</th>
              <th>Author</th>
              <th>Borrowed At</th>
            </tr>
          </thead>
          <tbody>
            {borrowedBooks.map((item) => (
              <tr key={item._id}>
                <td>{item.book?.title}</td>
                <td>{item.book?.author}</td>
                <td>{new Date(item.borrowedAt).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BorrowedList;
