import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container } from "reactstrap";
import { fetchBooks } from "../Features/BookSlice";
import { fetchBorrowedBooks } from "../Features/BorrowedBookModelSlice";

const Dashboard = () => {
  const dispatch = useDispatch();

  // جلب البيانات من Redux
  const user = useSelector((state) => state.user.user);
  const books = useSelector((state) => state.books.books);
  const borrowed = useSelector((state) => state.borrowed.items);

  useEffect(() => {
    dispatch(fetchBooks());
    if (user?.username) {
      dispatch(fetchBorrowedBooks(user.username));
    }
  }, [dispatch, user?.username]);

  return (
    <Container className="py-4">
      <div style={{ textAlign: "left" }}>
        {/* User image on top */}
        <img
          src={student}
          alt="user"
          style={{ width: "120px", borderRadius: "50%", marginBottom: "20px" }}
        />

        {/* Welcome message */}
        <h4>Welcome, {user?.fullName || "Guest"}!</h4>

        {/* Books statistics */}
        <div style={{ marginTop: "20px" }}>
          <p>Total Books: {books.length}</p>
          <p>Borrowed Books: {borrowed.length}</p>
        </div>

        {/* Location component */}
        <div className="mt-4">
          <Location />
        </div>
      </div>
    </Container>
  );
};

export default Dashboard;
