import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Input, FormGroup, Label } from "reactstrap";

const ViewBooks = () => {
  const [books, setBooks] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const username = localStorage.getItem("username");

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get("http://localhost:3001/books");
      setBooks(Array.isArray(res.data) ? res.data : []);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError("Failed to fetch books. Make sure server is running on port 3001.");
      setLoading(false);
    }
  };

  const handleBorrow = async (bookId) => {
    try {
      const res = await axios.post("http://localhost:3001/borrow", { username, bookId });
      alert(res.data.message || "Book borrowed successfully");
    } catch (err) {
      console.error(err);
      alert("Error borrowing book");
    }
  };

  const filteredBooks = books.filter((b) =>
    b.title?.toLowerCase().includes(searchTitle.toLowerCase())
  );

  if (loading) return <Container><p>Loading books...</p></Container>;
  if (error) return <Container><p style={{ color: "red" }}>{error}</p></Container>;

  return (
    <Container className="py-4">
      <FormGroup>
        <Label for="search">Search by Title</Label>
        <Input
          id="search"
          placeholder="Enter book title..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
      </FormGroup>

      <Row>
        {filteredBooks.length > 0 ? (
          filteredBooks.map((b) => (
            <Col md="4" className="mb-3" key={b._id || b.title}>
              <Card className="shadow-sm">
                <CardBody>
                  <h5>{b.title || "Untitled"}</h5>
                  <p>Author: {b.author || "Unknown"}</p>
                  <p>Year: {b.year || "Unknown"}</p>
                  <Button color="success" onClick={() => handleBorrow(b._id)}>
                    Borrow
                  </Button>
                </CardBody>
              </Card>
            </Col>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </Row>
    </Container>
  );
};

export default ViewBooks;
