import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, Row, Col, Card, CardBody, Button, Input } from "reactstrap";
import { fetchAllBorrowedBooks, returnBook } from "../Features/BorrowedBookModelSlice";

const AdminBorrowedBooks = () => {
  const dispatch = useDispatch();
  const { borrowedBooks, loading, error, successMessage } = useSelector(state => state.borrowed);

  const [search, setSearch] = useState("");
  const [localSuccess, setLocalSuccess] = useState("");

  // جلب جميع الكتب المقترضة عند التحميل
  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  // معالجة زر Return Book
  const handleReturn = async (borrowedId) => {
    await dispatch(returnBook({ _id: borrowedId }));
    setLocalSuccess("✔️ Book returned successfully!");
    setTimeout(() => {
      setLocalSuccess("");
      dispatch(fetchAllBorrowedBooks());
    }, 800);
  };

  // فلترة الكتب حسب البحث
  const filtered = borrowedBooks.filter(b => {
    const title = b.book?.title || "Deleted Book";
    return (
      b.studentUsername?.toLowerCase().includes(search.toLowerCase()) ||
      title.toLowerCase().includes(search.toLowerCase())
    );
  });

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">Borrowed Books List (Admin)</h3>

      {localSuccess && <div className="alert alert-success text-center">{localSuccess}</div>}
      {successMessage && <div className="alert alert-success text-center">{successMessage}</div>}
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}

      <Input
        placeholder="Search by student or book title..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-3"
      />

      {filtered.length === 0 && !loading && (
        <p className="text-center">No borrowed books found.</p>
      )}

      <Row>
        {filtered.map(b => {
          const book = b.book || { title: "Deleted Book", author: "-", year: "-" };
          const isReturned = !!b.returnedAt;

          return (
            <Col md="4" className="mb-3" key={b._id}>
              <Card className="shadow-sm">
                <CardBody>
                  <h5>{book.title}</h5>
                  <p>Author: {book.author}</p>
                  <p>Year: {book.year}</p>
                  <p>Student: {b.studentUsername}</p>
                  <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                  {isReturned && <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>}

                  {!isReturned && (
                    <Button color="success" onClick={() => handleReturn(b._id)}>
                      Return Book
                    </Button>
                  )}
                </CardBody>
              </Card>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export default AdminBorrowedBooks;
