import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card } from "reactstrap";
import axios from "axios";

const Dashboard = () => {
  const user = useSelector((state) => state.user.user);

  // Book states
  const [borrowedCount, setBorrowedCount] = useState(0);
  const [availableCount, setAvailableCount] = useState(0);

  useEffect(() => {
    // Example: fetch borrowed books count from API
    if (user?.username) {
      axios.get(`http://localhost:3001/borrowedbooks/${user.username}`)
        .then(res => setBorrowedCount(res.data.length))
        .catch(err => console.log(err));

      axios.get(`http://localhost:3001/availablebooks`)
        .then(res => setAvailableCount(res.data.length))
        .catch(err => console.log(err));
    }
  }, [user]);

  return (
    <Container className="py-4">
      <Row>
        <Col md={4} style={{ textAlign: "center" }}>
          {/* User image */}
          <img
            src={student}
            alt="user"
            style={{ width: "120px", borderRadius: "50%", marginBottom: "10px" }}
          />
          {/* User name */}
          <h4>{user?.fullName || "Guest"}</h4>
          {/* Welcome message */}
          <p>Welcome, {user?.fullName || "Guest"}!</p>
          {/* Location component */}
          <Location />
        </Col>

        <Col md={8}>
          <Row>
            <Col>
              <Card body className="text-center">
                <h5>Borrowed Books</h5>
                <p>{borrowedCount}</p>
              </Card>
            </Col>
            <Col>
              <Card body className="text-center">
                <h5>Available Books</h5>
                <p>{availableCount}</p>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
