import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card, CardBody } from "reactstrap";
import axios from "axios";

const Dashboard = () => {
  const user = useSelector((state) => state.user.user);
  const [booksCount, setBooksCount] = useState(0);
  const [borrowedCount, setBorrowedCount] = useState(0);

  useEffect(() => {
    // Replace URLs with your API endpoints
    const fetchCounts = async () => {
      try {
        const booksRes = await axios.get("http://localhost:3001/books"); // all books
        const borrowedRes = await axios.get(
          `http://localhost:3001/borrowedbooks/${user?.username}`
        ); // user's borrowed books

        setBooksCount(booksRes.data.length);
        setBorrowedCount(borrowedRes.data.length);
      } catch (error) {
        console.error("Error fetching counts:", error);
      }
    };

    if (user?.username) {
      fetchCounts();
    }
  }, [user]);

  return (
    <Container className="py-4">
      <Row>
        {/* User image and name */}
        <Col md="4" style={{ textAlign: "center" }}>
          <img
            src={student}
            alt="user"
            style={{
              width: "120px",
              borderRadius: "50%",
              marginBottom: "20px",
            }}
          />
          <h4>Welcome, {user?.fullName || "Guest"}!</h4>
          <div className="mt-3">
            <Location />
          </div>
        </Col>

        {/* Books statistics */}
        <Col md="8">
          <Row>
            <Col md="6">
              <Card className="mb-3">
                <CardBody style={{ textAlign: "center" }}>
                  <h5>Total Books</h5>
                  <h2>{booksCount}</h2>
                </CardBody>
              </Card>
            </Col>
            <Col md="6">
              <Card className="mb-3">
                <CardBody style={{ textAlign: "center" }}>
                  <h5>Borrowed Books</h5>
                  <h2>{borrowedCount}</h2>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
