import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, Row, Col, Card, CardBody, Button, Input } from "reactstrap";
import { fetchAllBorrowedBooks, returnBook } from "../Features/BorrowedBookModelSlice";

const AdminBorrowedBooks = () => {
  const dispatch = useDispatch();

  const { borrowedBooks, loading, error, successMessage } = useSelector(
    (state) => state.borrowed
  );

  const [search, setSearch] = useState("");

  // Ratings & feedback state
  const [ratings, setRatings] = useState({});
  const [feedbacks, setFeedbacks] = useState({});

  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  useEffect(() => {
    const r = {};
    const f = {};

    borrowedBooks.forEach((b) => {
      if (b.book) {
        r[b.book._id] = b.rating || 0;
        f[b.book._id] = b.feedback || "";
      }
    });

    setRatings(r);
    setFeedbacks(f);
  }, [borrowedBooks]);

  const handleReturn = async (borrowedId, bookId) => {
    const rating = ratings[bookId] || 0;
    const feedback = feedbacks[bookId] || "";

    await dispatch(returnBook({ _id: borrowedId, rating, feedback }));

    setTimeout(() => {
      dispatch(fetchAllBorrowedBooks());
    }, 500);
  };

  // Filtered books based on student username or book title
  const filtered = borrowedBooks.filter(
    (b) =>
      (b.studentUsername &&
        b.studentUsername.toLowerCase().includes(search.toLowerCase())) ||
      (b.book && b.book.title.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <Container className="py-4">
      {successMessage && (
        <div className="alert alert-success text-center fw-bold">
          {successMessage}
        </div>
      )}

      <h3 className="mb-4 text-center">Borrowed Books List (Admin)</h3>

      <Input
        placeholder="Search by student or book title..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4"
      />

      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}

      <Row>
        {filtered.length === 0 && !loading && (
          <p className="text-center">No borrowed books found.</p>
        )}

        {filtered.map((b) => {
          if (!b.book) return null;

          const isReturned = !!b.returnedAt;

          return (
            <Col md="4" className="mb-3" key={b._id}>
              <Card className="shadow-sm">
                <CardBody>
                  <h5>{b.book.title}</h5>
                  <p>Author: {b.book.author}</p>
                  <p>Year: {b.book.year}</p>
                  <p>Student: {b.studentUsername}</p>
                  <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                  {b.returnedAt && (
                    <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>
                  )}

                  {/* Rating */}
                  <div className="mb-2">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <span
                        key={star}
                        style={{
                          cursor: isReturned ? "not-allowed" : "pointer",
                          color: star <= (ratings[b.book._id] || 0) ? "gold" : "gray",
                          fontSize: "1.2rem",
                          marginRight: "3px",
                        }}
                        onClick={() =>
                          !isReturned &&
                          setRatings((prev) => ({ ...prev, [b.book._id]: star }))
                        }
                      >
                        â˜…
                      </span>
                    ))}
                  </div>

                  {/* Feedback */}
                  <Input
                    type="textarea"
                    placeholder="Leave feedback"
                    value={feedbacks[b.book._id] || ""}
                    disabled={isReturned}
                    onChange={(e) =>
                      !isReturned &&
                      setFeedbacks((prev) => ({ ...prev, [b.book._id]: e.target.value }))
                    }
                    className="mb-2"
                  />

                  {/* Return button */}
                  {!isReturned && (
                    <Button
                      color="success"
                      onClick={() => handleReturn(b._id, b.book._id)}
                    >
                      Return Book
                    </Button>
                  )}
                </CardBody>
              </Card>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export default AdminBorrowedBooks;
