import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";

const SERVER_URL = "http://localhost:3001";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  // â¬…ï¸ Fetch user from localStorage
  const username = localStorage.getItem("username");

  // ðŸ”¹ Get borrowed books
  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/borrowedbooks/${username}`);
      setBorrowedBooks(res.data);
    } catch (error) {
      console.error("Error fetching borrowed books:", error);
    }
  };

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  // ðŸ”¹ Handle Return
  const handleReturn = async (bookId) => {
    try {
      const res = await axios.put(`${SERVER_URL}/returnbook/${bookId}`);

      if (res.status === 200) {
        setSuccessMessage("âœ”ï¸ Successfully Returned!");

        // ØªØ­ÙˆÙŠÙ„ Ù„ØµÙØ­Ø© Home Ø¨Ø¹Ø¯ 1.5 Ø«Ø§Ù†ÙŠØ©
        setTimeout(() => {
          navigate("/home");
        }, 1500);
      }
    } catch (error) {
      console.error("Error returning book:", error);
    }
  };

  return (
    <Container className="mt-4">

      {/* ðŸ”¹ Success Message */}
      {successMessage && (
        <div className="alert alert-success text-center fw-bold">
          {successMessage}
        </div>
      )}

      <h2 className="text-center mb-4">My Borrowed Books</h2>

      <Row>
        {borrowedBooks.length === 0 ? (
          <p className="text-center">No borrowed books.</p>
        ) : (
          borrowedBooks.map((book) => (
            <Col md="4" className="mb-4" key={book.id}>
              <Card>
                <img
                  src={book.image}
                  alt={book.title}
                  className="img-fluid"
                  style={{ height: "250px", objectFit: "cover" }}
                />
                <CardBody>
                  <h5>{book.title}</h5>
                  <p><strong>Author:</strong> {book.author}</p>
                  <p><strong>Borrow Date:</strong> {book.borrowDate}</p>

                  <Button
                    color="success"
                    className="w-100"
                    onClick={() => handleReturn(book.id)}
                  >
                    Return Book
                  </Button>
                </CardBody>
              </Card>
            </Col>
          ))
        )}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
