import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container } from "reactstrap";
import { fetchBooks } from "../Features/BookSlice";
import { fetchBorrowedBooks } from "../Features/BorrowedBookModelSlice";

const Dashboard = () => {
  const dispatch = useDispatch();

  const user = useSelector((state) => state.user.user);

  const books = useSelector((state) => state.books.books);
  const booksLoading = useSelector((state) => state.books.isLoading);
  const booksError = useSelector((state) => state.books.isError);

  const borrowed = useSelector((state) => state.borrowed.items);
  const borrowedLoading = useSelector((state) => state.borrowed.isLoading);
  const borrowedError = useSelector((state) => state.borrowed.isError);

  // ✅ Fetch data on mount or user change
  useEffect(() => {
    dispatch(fetchBooks());

    if (user && user.username) {
      dispatch(fetchBorrowedBooks(user.username));
    }
  }, [dispatch, user]);

  // ✅ Optional: Refresh borrowed books every 10 seconds automatically
  useEffect(() => {
    if (!user?.username) return;
    const interval = setInterval(() => {
      dispatch(fetchBorrowedBooks(user.username));
    }, 10000); // كل 10 ثواني

    return () => clearInterval(interval);
  }, [dispatch, user]);

  return (
    <Container className="py-4">
     <div style={{ display: "flex", alignItems: "center", gap: "50px" }}>
  {/* User info */}
  <div style={{ textAlign: "center" }}>
    <img
      src={student}
      alt="user"
      style={{ width: "120px", borderRadius: "50%", marginBottom: "10px" }}
    />
    {/* هنا الاسم */}
    <h4>
      Welcome, {user?.fullName || localStorage.getItem("username") || "Guest"}!
    </h4>
    <div className="mt-3">
      <Location />
    </div>
 

  {/* Books statistics ... */}
</div>


        {/* Books statistics */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <div
            style={{
              backgroundColor: "#f0f0f0",
              padding: "20px",
              borderRadius: "10px",
              textAlign: "center",
              minWidth: "150px",
            }}
          >
            <h5>Total Books</h5>
            {booksLoading ? <h2>Loading...</h2> : booksError ? <h2>Error</h2> : <h2>{books.length}</h2>}
          </div>

          <div
            style={{
              backgroundColor: "#f0f0f0",
              padding: "20px",
              borderRadius: "10px",
              textAlign: "center",
              minWidth: "150px",
            }}
          >
            <h5>Borrowed Books</h5>
            {borrowedLoading ? (
              <h2>Loading...</h2>
            ) : borrowedError ? (
              <h2>Error</h2>
            ) : (
              <h2>{borrowed?.length ?? 0}</h2>
            )}
          </div>
        </div>
      </div>
    </Container>
  );
};

export default Dashboard;
