import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody } from "reactstrap";

const BorrowedList = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get("http://localhost:3001/borrowedbooks/all");
      setBorrowedBooks(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">All Borrowed Books</h3>
      <Row>
        {borrowedBooks.length === 0 && <p className="text-center">No borrowed books.</p>}
        {borrowedBooks.map((b) => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.book ? b.book.title : "Book deleted"}</h5>
                <p>Author: {b.book ? b.book.author : "-"}</p>
                <p>Year: {b.book ? b.book.year : "-"}</p>
                <p>Borrowed By: {b.studentUsername}</p>
                <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>

                {b.returnedAt ? (
                  <>
                    <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>
                    <p>Rating: {b.rating || "-"}</p>
                    <p>Feedback: {b.feedback || "-"}</p>
                  </>
                ) : (
                  <p>Status: Still Borrowed</p>
                )}
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default BorrowedList;
