// src/Features/BorrowedBookModelSlice.js
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const SERVER_URL = "http://localhost:3001";

// 1️⃣ Fetch borrowed books for a user
export const fetchBorrowedBooks = createAsyncThunk(
  "borrowed/fetchBorrowedBooks",
  async (username, { rejectWithValue }) => {
    try {
      const res = await axios.get(`${SERVER_URL}/borrowedbooks/${username}`);
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || "Error fetching borrowed books");
    }
  }
);

// 2️⃣ Borrow a book
export const borrowBook = createAsyncThunk(
  "borrowed/borrowBook",
  async ({ username, bookId }, { rejectWithValue }) => {
    try {
      const res = await axios.post(`${SERVER_URL}/borrow`, { username, bookId });
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || "Error borrowing book");
    }
  }
);

// 3️⃣ Return a book with optional rating & feedback
export const returnBook = createAsyncThunk(
  "borrowed/returnBook",
  async ({ borrowedId, rating, feedback }, { rejectWithValue }) => {
    try {
      const res = await axios.put(`${SERVER_URL}/borrowedbooks/return/${borrowedId}`, {
        rating,
        feedback,
      });
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || "Error returning book");
    }
  }
);

const initialState = {
  borrowedBooks: [],
  isLoading: false,
  isSuccess: false,
  isError: false,
  errorMessage: "",
};

const BorrowedBookModelSlice = createSlice({
  name: "borrowed",
  initialState,
  reducers: {
    resetBorrowedState: (state) => {
      state.isError = false;
      state.isSuccess = false;
      state.errorMessage = "";
    },
  },
  extraReducers: (builder) => {
    builder
      // Fetch borrowed books
      .addCase(fetchBorrowedBooks.pending, (state) => { state.isLoading = true; })
      .addCase(fetchBorrowedBooks.fulfilled, (state, action) => {
        state.isLoading = false;
        state.borrowedBooks = action.payload;
      })
      .addCase(fetchBorrowedBooks.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.errorMessage = action.payload;
      })

      // Borrow book
      .addCase(borrowBook.pending, (state) => { state.isLoading = true; })
.addCase(borrowBook.fulfilled, (state, action) => {
  state.isLoading = false;
  state.isSuccess = true;
  state.borrowedBooks.push(action.payload.data); // احرص على استخدام action.payload.data
})

      
      .addCase(borrowBook.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.errorMessage = action.payload;
      })

// Return book
.addCase(returnBook.fulfilled, (state, action) => {
  state.isLoading = false;
  state.isSuccess = true;

  state.borrowedBooks = state.borrowedBooks.filter(
    b => b._id !== action.payload.data?._id
  );
})
.addCase(returnBook.rejected, (state, action) => {
  state.isLoading = false;
  state.isError = true;
  state.errorMessage = action.payload;
})

  }
});

export const { resetBorrowedState } = BorrowedBookModelSlice.actions;
export default BorrowedBookModelSlice.reducer;
