import React from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card } from "reactstrap";

const Dashboard = () => {
  const user = useSelector((state) => state.user?.user);

  // تأكد من استخدام أسماء slices الصحيحة من store
  const borrowedBooks = useSelector((state) => state.borrowedBookModel?.list || []);
  const books = useSelector((state) => state.book?.list || []);

  // حساب عدد الكتب المستعارة لهذا المستخدم
  const borrowedCount = borrowedBooks.filter(b => b.username === user?.username).length;

  // عدد الكتب المتاحة = كل الكتب
  const availableCount = books.length;

  const username = user?.username || "Guest";

  return (
    <Container className="py-4">
      <Row>
        <Col md={4} className="text-center">
          <img
            src={student}
            alt="user"
            style={{ width: "120px", borderRadius: "50%", marginBottom: "10px" }}
          />
          <h4>{username}</h4>
          <p>Welcome, {username}!</p>
          <Location />
        </Col>

        <Col md={8}>
          <Row>
            <Col>
              <Card body style={{ textAlign: "center", marginBottom: "10px" }}>
                <h5>Borrowed Books</h5>
                <p>{borrowedCount}</p>
              </Card>
            </Col>
            <Col>
              <Card body style={{ textAlign: "center", marginBottom: "10px" }}>
                <h5>Available Books</h5>
                <p>{availableCount}</p>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
