import { useState } from "react";
import { Link } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, Form, FormGroup, Label, Input, Button, Alert } from "reactstrap";

const Login = () => {
  const [message, setMessage] = useState({ text: "", type: "" }); // type: 'success' | 'danger'

  const handleSubmit = (e) => {
    e.preventDefault();

    const username = e.target.username.value;
    const password = e.target.password.value;

    // تحقق بسيط
    if (username === "admin" && password === "12345") {
      setMessage({ text: "نجاح المهمة! تم تسجيل الدخول بنجاح.", type: "success" });
    } else {
      setMessage({ text: "خطأ! اسم المستخدم أو كلمة المرور غير صحيحة.", type: "danger" });
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <Row className="w-100 justify-content-center">
        <Col md="4">
          <Card className="shadow-lg p-4">
            <CardBody>
              <h3 className="text-center mb-4">Login</h3>

              {/* الرسالة تظهر فوق الفورم */}
              {message.text && (
                <Alert color={message.type} className="text-center mb-3">
                  {message.text}
                </Alert>
              )}

              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="username">Username</Label>
                  <Input type="text" id="username" name="username" placeholder="Enter username" required />
                </FormGroup>

                <FormGroup>
                  <Label for="password">Password</Label>
                  <Input type="password" id="password" name="password" placeholder="Enter password" required />
                </FormGroup>

                <FormGroup tag="fieldset">
                  <Label>Role</Label>
                  <FormGroup check>
                    <Input type="radio" name="role" value="student" defaultChecked />
                    <Label check>Student</Label>
                  </FormGroup>
                  <FormGroup check>
                    <Input type="radio" name="role" value="admin" />
                    <Label check>Admin</Label>
                  </FormGroup>
                </FormGroup>

                <Button color="primary" className="w-100 mt-3" type="submit">
                  Login
                </Button>

                <div className="text-center mt-3">
                  <span>Don't have an account? </span>
                  <Link to="/register">Register</Link>
                </div>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
