import { useEffect, useState } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Input
} from "reactstrap";
import { useNavigate } from "react-router-dom";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [feedbacks, setFeedbacks] = useState({});
  const [ratings, setRatings] = useState({});
  const username = localStorage.getItem("username");
  const navigate = useNavigate();

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  // üü¶ Fetch borrowed books
  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get(
        `http://localhost:3001/borrowedbooks/${username}`
      );
      setBorrowedBooks(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  // ‚≠ê When clicking a star
  const handleStarClick = (bookId, star) => {
    setRatings((prev) => ({ ...prev, [bookId]: star }));
  };

  // üü© Return a book + save rating + save feedback
  const handleReturn = async (borrowedId, bookId) => {
    try {
      const rating = ratings[bookId] || 0;
      const feedback = feedbacks[bookId] || "";

      // 1Ô∏è‚É£ Save rating
      if (rating > 0) {
        await axios.post("http://localhost:3001/books/rate", {
          username,
          bookId,
          rating,
          feedback, // send feedback also (optional)
        });
      }

      // 2Ô∏è‚É£ delete the borrowed item
      await axios.delete(
        `http://localhost:3001/borrowedbooks/${borrowedId}`
      );

      alert("Book returned successfully!");
      fetchBorrowedBooks(); // refresh after deleting
    } catch (err) {
      console.error("Error returning book:", err);
      alert("Error returning book");
    }
  };

  const handleFeedbackChange = (bookId, value) => {
    setFeedbacks((prev) => ({ ...prev, [bookId]: value }));
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">My Borrowed Books</h3>

      <Row>
        {borrowedBooks.length === 0 && (
          <p className="text-center">No borrowed books.</p>
        )}

        {borrowedBooks.map((b) => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.book.title}</h5>
                <p>Author: {b.book.author}</p>
                <p>Year: {b.book.year}</p>
                <p>
                  Borrowed At:{" "}
                  {new Date(b.borrowedAt).toLocaleDateString()}
                </p>

                {/* ‚≠ê Rating stars */}
                <div className="mb-2">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <span
                      key={star}
                      style={{
                        cursor: "pointer",
                        color:
                          star <= (ratings[b.book._id] || 0)
                            ? "gold"
                            : "gray",
                        fontSize: "1.3rem",
                        marginRight: "4px",
                      }}
                      onClick={() => handleStarClick(b.book._id, star)}
                    >
                      ‚òÖ
                    </span>
                  ))}
                </div>

                {/* ‚úç Feedback */}
                <Input
                  type="textarea"
                  placeholder="Leave feedback"
                  value={feedbacks[b.book._id] || ""}
                  onChange={(e) =>
                    handleFeedbackChange(b.book._id, e.target.value)
                  }
                  className="mb-3"
                />

                {/* üîô Return button */}
                <Button
                  color="danger"
                  block
                  onClick={() =>
                    handleReturn(b._id, b.book._id)
                  }
                >
                  Return Book
                </Button>
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
