// src/Component/BorrowedList.js
import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchAllBorrowedBooks } from "../Features/BorrowedBookModelSlice";
import { Container, Table, Input } from "reactstrap";

const BorrowedList = () => {
  const dispatch = useDispatch();
  const { borrowedBooks, loading, error } = useSelector((state) => state.borrowed);
  const [search, setSearch] = useState("");

  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  // فلترة آمنة لتجنب null/undefined
  const filtered = borrowedBooks.filter((b) =>
    (b.studentUsername?.toLowerCase().includes(search.toLowerCase())) ||
    (b.book?.title?.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <Container className="py-4">
      <h2 className="mb-4">Borrowed Books List (Admin)</h2>

      <Input
        placeholder="Search by student or book title"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-3"
      />

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : filtered.length === 0 ? (
        <p>No borrowed books found.</p>
      ) : (
        <Table striped responsive>
          <thead>
            <tr>
              <th>Student</th>
              <th>Book</th>
              <th>Author</th>
              <th>Year</th>
              <th>Borrowed At</th>
              <th>Returned At</th>
              <th>Rating</th>
              <th>Feedback</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map((b) => (
              <tr key={b._id}>
                <td>{b.studentUsername}</td>
                <td>{b.book?.title || "Deleted book"}</td>
                <td>{b.book?.author || "-"}</td>
                <td>{b.book?.year || "-"}</td>
                <td>{new Date(b.borrowedAt).toLocaleString()}</td>
                <td>{b.returnedAt ? new Date(b.returnedAt).toLocaleString() : "-"}</td>
                <td>{b.rating || 0}</td>
                <td>{b.feedback || "-"}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      <footer className="mt-5 text-center text-muted">
        © 2025 Library System. All rights reserved.
      </footer>
    </Container>
  );
};

export default BorrowedList;
