// src/Component/BorrowList.js
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Container, Table } from "reactstrap";
import axios from "axios";

const BorrowList = () => {
  const [borrowedList, setBorrowedList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);

  useEffect(() => {
    fetchBorrowedList();
  }, []);

  const fetchBorrowedList = async () => {
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:3001/borrowedbooks/all");
      setBorrowedList(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
    setLoading(false);
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">All Borrowed Books</h3>
      {loading ? (
        <p>Loading...</p>
      ) : borrowedList.length === 0 ? (
        <p className="text-center">No borrowed books found.</p>
      ) : (
        <Table bordered hover responsive>
          <thead>
            <tr>
              <th>Student Username</th>
              <th>Book Title</th>
              <th>Author</th>
              <th>Year</th>
              <th>Borrowed At</th>
              <th>Returned At</th>
              <th>Rating</th>
              <th>Feedback</th>
            </tr>
          </thead>
          <tbody>
            {borrowedList.map(b => (
              <tr key={b._id}>
                <td>{b.studentUsername}</td>
                <td>{b.book?.title}</td>
                <td>{b.book?.author}</td>
                <td>{b.book?.year}</td>
                <td>{new Date(b.borrowedAt).toLocaleDateString()}</td>
                <td>{b.returnedAt ? new Date(b.returnedAt).toLocaleDateString() : "-"}</td>
                <td>{b.rating || 0}</td>
                <td>{b.feedback || "-"}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default BorrowList;
