import { Link, useNavigate } from "react-router-dom";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink, Button } from "reactstrap";
import logoImg from "../images/logo.png"; // مسار شعارك

const Header = ({ userRole, setUserRole }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    setUserRole(null); // مسح الدور
    navigate("/login"); // إعادة التوجيه للصفحة الرئيسية أو Login
  };

  return (
    <Navbar color="light" light expand="md" className="mb-4 px-3">
      {/* Logo */}
      <NavbarBrand tag={Link} to="/">
        <img src={logoImg} alt="Logo" style={{ height: "40px" }} />
      </NavbarBrand>

      <Nav className="me-auto" navbar>
        <NavItem>
          <NavLink tag={Link} to="/">Home</NavLink>
        </NavItem>

        {/* Links حسب الدور */}
        {userRole === "student" && (
          <NavItem>
            <NavLink tag={Link} to="/dashboard">Student</NavLink>
          </NavItem>
        )}
        {userRole === "admin" && (
          <NavItem>
            <NavLink tag={Link} to="/admin">Admin</NavLink>
          </NavItem>
        )}
      </Nav>

      <Nav navbar>
        {!userRole && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/login">Login</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/register">Register</NavLink>
            </NavItem>
          </>
        )}

        {/* Logout يظهر فقط بعد تسجيل الدخول */}
        {userRole && (
          <NavItem>
            <Button color="danger" onClick={handleLogout} className="ms-2">
              Logout
            </Button>
          </NavItem>
        )}
      </Nav>
    </Navbar>
  );
};

export default Header;
