// src/Component/Register.js
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { registerSchema } from "../Validations/UserValidations";

const Register = () => {
  const navigate = useNavigate();

  const {
    register: registerField,
    handleSubmit,
    formState: { errors }
  } = useForm({
    resolver: yupResolver(registerSchema)
  });

  const onSubmit = async (data) => {
    try {
      const res = await axios.post("http://localhost:3001/register", {
        fullName: data.fullName,
        username: data.username,
        password: data.password
      });

      if (res.data.success) {
        alert("Account created successfully!");
        navigate("/login");
      } else {
        alert(res.data.message);
      }
    } catch {
      alert("Server error");
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <Row className="w-100 justify-content-center">
        <Col md="5">
          <Card className="shadow-lg p-3">
            <CardBody>
              <h3 className="text-center mb-4">Create Account</h3>
              <Form onSubmit={handleSubmit(onSubmit)}>
                <FormGroup>
                  <Label>Full Name</Label>
                  <Input type="text" {...registerField("fullName")} />
                  {errors.fullName && <small className="text-danger">{errors.fullName.message}</small>}
                </FormGroup>

                <FormGroup>
                  <Label>Username</Label>
                  <Input type="text" {...registerField("username")} />
                  {errors.username && <small className="text-danger">{errors.username.message}</small>}
                </FormGroup>

                <FormGroup>
                  <Label>Password</Label>
                  <Input type="password" {...registerField("password")} />
                  {errors.password && <small className="text-danger">{errors.password.message}</small>}
                </FormGroup>

                <FormGroup>
                  <Label>Confirm Password</Label>
                  <Input type="password" {...registerField("confirmPassword")} />
                  {errors.confirmPassword && <small className="text-danger">{errors.confirmPassword.message}</small>}
                </FormGroup>

                <Button color="success" className="w-100 mt-3" type="submit">
                  Register
                </Button>
              </Form>

              <div className="text-center mt-3">
                <span>Already have an account? </span>
                <a href="/login">Login</a>
              </div>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
