import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, NavItem, NavLink, Button } from "reactstrap";
import { useState, useEffect } from "react";

const Header = () => {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState(null);

  // قراءة الدور عند تحميل المكون
  useEffect(() => {
    const role = localStorage.getItem("userRole");
    if (role) setUserRole(role);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole"); // مسح الدور
    setUserRole(null); // تحديث State لإخفاء الزر
    navigate("/login"); // إعادة التوجيه للـ login
  };

  return (
    <Navbar color="light" light expand="md" className="mb-4">
      <Nav className="me-auto" navbar>
        <NavItem>
          <NavLink tag={Link} to="/">Home</NavLink>
        </NavItem>

        {userRole === "student" && (
          <NavItem>
            <NavLink tag={Link} to="/student-dashboard">Student</NavLink>
          </NavItem>
        )}
        {userRole === "admin" && (
          <NavItem>
            <NavLink tag={Link} to="/admin-dashboard">Admin</NavLink>
          </NavItem>
        )}
      </Nav>

      {userRole && (
        <Nav className="ms-auto" navbar>
          <NavItem>
            <Button color="danger" onClick={handleLogout}>Logout</Button>
          </NavItem>
        </Nav>
      )}
    </Navbar>
  );
};

export default Header;
