import { useEffect, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import axios from "axios";
import {
  Container,
  Card,
  CardBody,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
} from "reactstrap";

const AddEditBook = () => {
  const [params] = useSearchParams();
  const bookId = params.get("id");
  const navigate = useNavigate();

  const [form, setForm] = useState({
    title: "",
    author: "",
    year: "",
  });

  // Load book data for edit
  useEffect(() => {
    if (bookId) {
      axios.get(`http://localhost:3001/books/${bookId}`).then((res) => {
        setForm(res.data);
      });
    }
  }, [bookId]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (bookId) {
      await axios.put(`http://localhost:3001/books/${bookId}`, form);
    } else {
      await axios.post("http://localhost:3001/books", form);
    }

    navigate("/admin");
  };

  return (
    <Container className="py-4">
      <Card className="shadow p-3">
        <CardBody>
          <h3 className="text-center">
            {bookId ? "Edit Book" : "Add New Book"}
          </h3>

          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Book Title</Label>
              <Input
                value={form.title}
                onChange={(e) => setForm({ ...form, title: e.target.value })}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label>Author</Label>
              <Input
                value={form.author}
                onChange={(e) => setForm({ ...form, author: e.target.value })}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label>Year</Label>
              <Input
                type="number"
                value={form.year}
                onChange={(e) => setForm({ ...form, year: e.target.value })}
                required
              />
            </FormGroup>

            <Button color="success" className="w-100 mt-3">
              {bookId ? "Update Book" : "Save Book"}
            </Button>
          </Form>
        </CardBody>
      </Card>
    </Container>
  );
};

export default AddEditBook;
