import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, NavItem, NavLink, Button, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from "reactstrap";
import { useState, useEffect } from "react";

const Header = () => {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState(null);

  // قراءة الدور عند تحميل المكون
  useEffect(() => {
    const role = localStorage.getItem("userRole");
    if (role) setUserRole(role);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole"); // مسح الدور
    setUserRole(null); // تحديث State لإخفاء الزر والدائرة
    navigate("/login"); // إعادة التوجيه للـ login
  };

  return (
    <Navbar color="light" light expand="md" className="mb-4 px-3">
      <Nav className="me-auto" navbar>
        <NavItem>
          <NavLink tag={Link} to="/">Home</NavLink>
        </NavItem>

        {userRole === "student" && (
          <NavItem>
            <NavLink tag={Link} to="/student-dashboard">Student Dashboard</NavLink>
          </NavItem>
        )}
        {userRole === "admin" && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/admin">Admin Dashboard</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/book">Books</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/borrowedlist">Borrowed List</NavLink>
            </NavItem>
          </>
        )}
      </Nav>

      {userRole && (
        <Nav className="ms-auto" navbar className="d-flex align-items-center">
          {/* دائرة باسم الدور */}
          <UncontrolledDropdown inNavbar>
            <DropdownToggle
              nav
              caret
              style={{
                width: "40px",
                height: "40px",
                borderRadius: "50%",
                backgroundColor: "#007bff",
                color: "#fff",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontWeight: "bold",
                marginRight: "10px"
              }}
            >
              {userRole.charAt(0).toUpperCase()}
            </DropdownToggle>
            <DropdownMenu end>
              <DropdownItem header>{userRole.charAt(0).toUpperCase() + userRole.slice(1)}</DropdownItem>
              <DropdownItem divider />
              <DropdownItem onClick={handleLogout}>Logout</DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
        </Nav>
      )}
    </Navbar>
  );
};

export default Header;
