import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, NavItem, NavLink } from "reactstrap";
import { useState, useEffect } from "react";

const Header = () => {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState(null);

  // قراءة الدور عند تحميل المكون
  useEffect(() => {
    const role = localStorage.getItem("userRole");
    if (role) setUserRole(role);
  }, []);

  // دالة Logout
  const handleLogout = () => {
    localStorage.removeItem("userRole");
    setUserRole(null);
    navigate("/login");
  };

  return (
    <Navbar color="light" light expand="md" className="mb-4 px-3">
      <Nav className="me-auto" navbar>
        {/* Home link دائم */}
        <NavItem>
          <NavLink tag={Link} to="/home">Home</NavLink>
        </NavItem>

        {/* إذا لم يسجل الدخول → لا تظهر روابط أخرى */}
        {!userRole && null}

        {/* روابط الطالب */}
        {userRole === "student" && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/dashboard">Dashboard</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/viewbooks">View Books</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/myborrowed">My Borrowed Books</NavLink>
            </NavItem>
          </>
        )}

        {/* روابط الـ Admin */}
        {userRole === "admin" && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/admin">Admin Dashboard</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/book">Books</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/borrowedlist">Borrowed List</NavLink>
            </NavItem>
          </>
        )}
      </Nav>

      {/* دائرة باسم الدور بعد تسجيل الدخول */}
      {userRole && (
        <div
          style={{
            width: "40px",
            height: "40px",
            borderRadius: "50%",
            backgroundColor: userRole === "admin" ? "#007bff" : "#28a745",
            color: "#fff",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontWeight: "bold",
            marginLeft: "auto",
            cursor: "pointer"
          }}
          onClick={handleLogout} // يمكن الضغط عليها لتسجيل الخروج
          title="Click to Logout"
        >
          {userRole.charAt(0).toUpperCase()}
        </div>
      )}
    </Navbar>
  );
};

export default Header;
