import { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button } from "reactstrap";

const BorrowedList = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);


  return (
    <div className="p-4">
      <h3 className="mb-4 text-center">All Borrowed Books</h3>
      <Table bordered hover responsive>
        <thead>
          <tr>
            <th>Student Username</th>
            <th>Book Title</th>
            <th>Author</th>
            <th>Year</th>
            <th>Borrowed At</th>
            <th>Returned At</th>
            <th>Rating</th>
            <th>Feedback</th>
            <th>Action1</th>
          </tr>
        </thead>
        <tbody>
          {borrowedBooks.length === 0 ? (
            <tr>
              <td colSpan="9" className="text-center">No borrowed books</td>
            </tr>
          ) : (
            borrowedBooks.map(b => (
              <tr key={b._id}>
                <td>{b.studentUsername}</td>
                <td>{b.book?.title}</td>
                <td>{b.book?.author}</td>
                <td>{b.book?.year}</td>
                <td>{new Date(b.borrowedAt).toLocaleDateString()}</td>
                <td>{b.returnedAt ? new Date(b.returnedAt).toLocaleDateString() : "-"}</td>
                <td>{b.rating || "-"}</td>
                <td>{b.feedback || "-"}</td>
                <td>
                  {!b.returnedAt && (
                    <Button color="danger" size="sm" onClick={() => handleReturn(b._id)}>
                      Mark Returned
                    </Button>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default BorrowedList;
