import React from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card, CardBody } from "reactstrap";

const Dashboard = () => {
  const user = useSelector((state) => state.user.user);

  // Borrowed books by the student
  const borrowedBooks = useSelector((state) => state.borrowed.books) || [];

  // All available books in the library
  const allBooks = useSelector((state) => state.books.books) || [];

  // Calculate available books (not borrowed)
  const availableBooks = allBooks.length - borrowedBooks.length;

  return (
    <Container className="py-4">
      <div style={{ textAlign: "left" }}>
        {/* User image */}
        <img
          src={student}
          alt="user"
          style={{
            width: "120px",
            borderRadius: "50%",
            marginBottom: "20px",
          }}
        />

        {/* Welcome message */}
        <h3 style={{ fontWeight: "bold" }}>
          Welcome, {user?.fullName || "Student"} ðŸ‘‹
        </h3>
        <p className="text-muted">We hope you enjoy your reading experience âœ¨</p>

        {/* Books info cards */}
        <Row className="mt-4">
          <Col md="6">
            <Card>
              <CardBody>
                <h5>ðŸ“š Borrowed Books</h5>
                <h3 style={{ color: "#2980b9" }}>{borrowedBooks.length}</h3>
              </CardBody>
            </Card>
          </Col>

          <Col md="6">
            <Card>
              <CardBody>
                <h5>ðŸ“– Available Books</h5>
                <h3 style={{ color: "green" }}>{availableBooks}</h3>
              </CardBody>
            </Card>
          </Col>
        </Row>

        {/* Location component */}
        <div className="mt-4">
          <Location />
        </div>
      </div>
    </Container>
  );
};

export default Dashboard;
