import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Table } from "reactstrap";

const ViewBooks = () => {
  const [books, setBooks] = useState([]);

  const fetchBooks = async () => {
    const res = await axios.get("http://localhost:3001/books");
    setBooks(res.data);
  };

  const handleBorrow = async (bookId) => {
    const username = localStorage.getItem("username"); // تخزين username عند login
    const res = await axios.post("http://localhost:3001/borrow", { username, bookId });
    alert(res.data.message);
  };

  useEffect(() => { fetchBooks(); }, []);

  return (
    <Container className="py-4">
      <Row>
        <Col md="12">
          <Card className="shadow p-3">
            <CardBody>
              <h3 className="text-center mb-3">Available Books</h3>
              <Table bordered hover responsive>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Author</th>
                    <th>Year</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {books.map((b, index) => (
                    <tr key={b._id}>
                      <td>{index + 1}</td>
                      <td>{b.title}</td>
                      <td>{b.author}</td>
                      <td>{b.year}</td>
                      <td>
                        <Button color="success" onClick={() => handleBorrow(b._id)}>
                          Borrow
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ViewBooks;
