//import logo from "./logo.svg";
import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Container, Row } from "reactstrap";

// Correct imports based on your folder names
import Header from "./Component/Header";
import Footer from "./Component/Footer";
import Home from "./Component/Home";
import Login from "./Component/Login";
import Register from "./Component/Register";
import Dashboard from "./Component/Dashboard";
import ViewBooks from "./Component/ViewBooks";
import MyBorrowedBooks from "./Component/MyBorrowedBooks";
import AdminDashboard from "./Component/AdminDashboard";
import AddEditBook from "./Component/AddEditBook";
import BorrowedList from "./Component/BorrowedList";

const App = () => {
  return (
    <Container fluid>
      <Router>
        <Row>
          <Header />
        </Row>

        <Row className="main">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Student Routes */}
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/view-books" element={<ViewBooks />} />
            <Route path="/my-borrowed" element={<MyBorrowedBooks />} />

            {/* Admin Routes */}
            <Route path="/admin-dashboard" element={<AdminDashboard />} />
            <Route path="/add-edit-book" element={<AddEditBook />} />
            <Route path="/borrowed-list" element={<BorrowedList />} />
          </Routes>
        </Row>

        <Row>
          <Footer />
        </Row>
      </Router>
    </Container>
  );
};

export default App;
