import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Input } from "reactstrap";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [feedbacks, setFeedbacks] = useState({});
  const [ratings, setRatings] = useState({});
  const [returnedBooks, setReturnedBooks] = useState([]); // Track returned books
  const username = localStorage.getItem("username");

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/borrowedbooks/${username}`);
      setBorrowedBooks(res.data);

      // Preload ratings and feedbacks if any
      const initialRatings = {};
      const initialFeedbacks = {};
      res.data.forEach(b => {
        initialRatings[b.book._id] = b.rating || 0;
        initialFeedbacks[b.book._id] = b.feedback || "";
      });
      setRatings(initialRatings);
      setFeedbacks(initialFeedbacks);

    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  const handleStarClick = (bookId, star) => {
    setRatings(prev => ({ ...prev, [bookId]: star }));
  };

  const handleFeedbackChange = (bookId, value) => {
    setFeedbacks(prev => ({ ...prev, [bookId]: value }));
  };

  const handleReturn = async (borrowedId, bookId) => {
    try {
      const rating = ratings[bookId] || 0;
      const feedback = feedbacks[bookId] || "";

      await axios.put(`http://localhost:3001/borrowedbooks/return/${borrowedId}`, {
        rating,
        feedback
      });

      // Mark this book as returned so the button is disabled immediately
      setReturnedBooks(prev => [...prev, borrowedId]);

      alert("Book returned successfully!");
      fetchBorrowedBooks(); // optional: refresh the list
    } catch (err) {
      console.error(err);
      alert("Error returning book");
    }
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">My Borrowed Books</h3>
      <Row>
        {borrowedBooks.length === 0 && <p className="text-center">No borrowed books.</p>}
        {borrowedBooks.map(b => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.book?.title}</h5>
                <p>Author: {b.book?.author}</p>
                <p>Year: {b.book?.year}</p>
                <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                {b.returnedAt && <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>}

                <div className="mb-2">
                  {[1,2,3,4,5].map(star => (
                    <span
                      key={star}
                      style={{
                        cursor: "pointer",
                        color: star <= (ratings[b.book._id] || 0) ? "gold" : "gray",
                        fontSize: "1.2rem",
                        marginRight: "3px"
                      }}
                      onClick={() => handleStarClick(b.book._id, star)}
                    >
                      â˜…
                    </span>
                  ))}
                </div>

                <Input
                  type="textarea"
                  placeholder="Leave feedback"
                  value={feedbacks[b.book._id] || ""}
                  onChange={(e) => handleFeedbackChange(b.book._id, e.target.value)}
                  className="mb-2"
                />

                {/* Return button logic */}
                {!b.returnedAt && !returnedBooks.includes(b._id) && (
                  <Button color="success" onClick={() => handleReturn(b._id, b.book._id)}>
                    Return Book
                  </Button>
                )}

                {(b.returnedAt || returnedBooks.includes(b._id)) && (
                  <Button color="secondary" disabled>
                    Returned
                  </Button>
                )}

              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
