import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Container, Row, Col, Card, CardBody, Input } from "reactstrap";
import { fetchAllBorrowedBooks } from "../Features/BorrowedBookModelSlice";

const BorrowedList = () => {
  const dispatch = useDispatch();

  const { borrowedBooks, loading, error } = useSelector(state => state.borrowed);
  const [search, setSearch] = useState("");

  useEffect(() => {
    // جلب جميع الكتب المقترضة عند تحميل الصفحة
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  // فلترة حسب اسم الطالب أو عنوان الكتاب
  const filteredBooks = borrowedBooks.filter(b => {
    const studentMatch = b.studentUsername.toLowerCase().includes(search.toLowerCase());
    const titleMatch = b.book?.title?.toLowerCase().includes(search.toLowerCase());
    return studentMatch || titleMatch;
  });

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">All Borrowed Books</h3>

      {/* مربع البحث */}
      <div className="mb-3 text-center">
        <Input
          type="text"
          placeholder="Search by student or book..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="w-50 mx-auto"
        />
      </div>

      {loading && <p className="text-center">Loading...</p>}
      {error && <p className="text-center text-danger">{error}</p>}
      {filteredBooks.length === 0 && !loading && <p className="text-center">No borrowed books found.</p>}

      <Row>
        {filteredBooks.map(b => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.book?.title || "No Title"}</h5>
                <p><strong>Author:</strong> {b.book?.author || "N/A"}</p>
                <p><strong>Year:</strong> {b.book?.year || "N/A"}</p>
                <p><strong>Student:</strong> {b.studentUsername}</p>
                <p><strong>Borrowed At:</strong> {new Date(b.borrowedAt).toLocaleString()}</p>
                {b.returnedAt && (
                  <p><strong>Returned At:</strong> {new Date(b.returnedAt).toLocaleString()}</p>
                )}
                <p><strong>Rating:</strong> {b.rating}</p>
                <p><strong>Feedback:</strong> {b.feedback || "-"}</p>
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default BorrowedList;
