import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Input, FormGroup, Label } from "reactstrap";

const ViewBooks = () => {
  const [books, setBooks] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");

  const username = localStorage.getItem("username"); // تأكد أن username مخزن عند login

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get("http://localhost:3001/books");
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
    }
  };

  const handleBorrow = async (bookId) => {
    try {
      const res = await axios.post("http://localhost:3001/borrow", { username, bookId });
      if (res.data.success) {
        alert(res.data.message);
      } else {
        alert(res.data.message || "Error borrowing book");
      }
    } catch (err) {
      console.error("Error borrowing book:", err);
      alert("Error borrowing book");
    }
  };

  const handleRate = async (bookId, rating) => {
    try {
      const res = await axios.post("http://localhost:3001/books/rate", { username, bookId, rating });
      if (res.data.success) {
        alert(res.data.message);
        fetchBooks(); // تحديث قائمة الكتب بعد التقييم
      } else {
        alert(res.data.message || "Error rating book");
      }
    } catch (err) {
      console.error("Error rating book:", err);
      alert("Error rating book");
    }
  };

  const filteredBooks = books.filter((b) =>
    b.title.toLowerCase().includes(searchTitle.toLowerCase())
  );

  const getAverageRating = (book) => {
    if (!book.ratings || book.ratings.length === 0) return 0;
    const sum = book.ratings.reduce((acc, r) => acc + r.rating, 0);
    return (sum / book.ratings.length).toFixed(1);
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">Available Books</h3>

      <FormGroup>
        <Label for="search">Search by Title</Label>
        <Input
          id="search"
          placeholder="Enter book title..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
      </FormGroup>

      <Row>
        {filteredBooks.map((b) => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.title}</h5>
                <p>Author: {b.author}</p>
                <p>Year: {b.year}</p>

                {/* Average Rating */}
                <p>
                  Rating: {getAverageRating(b)}{" "}
                  <span role="img" aria-label="star">⭐</span>
                </p>

                {/* Stars for Student to Rate */}
                <div>
                  {[1, 2, 3, 4, 5].map((i) => (
                    <span
                      key={i}
                      style={{ cursor: "pointer", color: i <= (b.ratings?.find(r => r.studentUsername === username)?.rating || 0) ? "gold" : "gray", fontSize: "20px" }}
                      onClick={() => handleRate(b._id, i)}
                    >
                      ★
                    </span>
                  ))}
                </div>

                <Button color="success" className="mt-2" onClick={() => handleBorrow(b._id)}>
                  Borrow
                </Button>
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default ViewBooks;
