// src/Components/BorrowedList.js
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllBorrowedBooks } from "../Features/BorrowedBookModelSlice";
import { Table, Input, Container } from "reactstrap";

const BorrowedList = () => {
  const dispatch = useDispatch();
  const { borrowedBooks, loading, error } = useSelector((state) => state.borrowed);

  const [search, setSearch] = useState("");

  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  // ðŸ”¹ Filter with safety check
  const filtered = borrowedBooks.filter((b) => {
    const student = b.studentUsername || "";
    const title = b.book?.title || "";
    const s = search.toLowerCase();

    return student.toLowerCase().includes(s) || title.toLowerCase().includes(s);
  });

  if (loading) return <p>Loading borrowed books...</p>;
  if (error) return <p>Error: {error}</p>;
  if (borrowedBooks.length === 0) return <p>No borrowed books found.</p>;

  return (
    <Container className="py-4">
      <h2 className="mb-4">Borrowed Books List</h2>

      <Input
        type="text"
        placeholder="Search by student or book title..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-3"
      />

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Student</th>
            <th>Book Title</th>
            <th>Author</th>
            <th>Year</th>
            <th>Borrowed At</th>
            <th>Returned At</th>
            <th>Rating</th>
            <th>Feedback</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((b) => (
            <tr key={b._id}>
              <td>{b.studentUsername}</td>
              <td>{b.book?.title || "N/A"}</td>
              <td>{b.book?.author || "N/A"}</td>
              <td>{b.book?.year || "N/A"}</td>
              <td>{new Date(b.borrowedAt).toLocaleDateString()}</td>
              <td>{b.returnedAt ? new Date(b.returnedAt).toLocaleDateString() : "-"}</td>
              <td>{b.rating}</td>
              <td>{b.feedback}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default BorrowedList;
