import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button } from "reactstrap";

const BorrowedList = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);

  useEffect(() => {
    fetchBorrowed();
  }, []);

  const fetchBorrowed = async () => {
    try {
      const res = await axios.get("http://localhost:3001/borrowedbooks/all");
      setBorrowedBooks(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  const handleReturn = async (id) => {
    try {
      await axios.put(`http://localhost:3001/borrowedbooks/return/${id}`, {
        rating: 0,
        feedback: ""
      });
      alert("Book marked as returned!");
      fetchBorrowed();
    } catch (err) {
      console.log(err);
      alert("Error marking book as returned");
    }
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">Borrowed Books List</h3>
      <Row>
        {borrowedBooks.length === 0 && (
          <p className="text-center">No borrowed books found.</p>
        )}

        {borrowedBooks.map((item) => (
          <Col md="4" key={item._id}>
            <Card className="shadow-sm mb-3">
              <CardBody>
                <h5>{item.book?.title}</h5>
                <p><strong>Student:</strong> {item.studentUsername}</p>
                <p><strong>Author:</strong> {item.book?.author}</p>
                <p><strong>Borrowed At:</strong> {new Date(item.borrowedAt).toLocaleDateString()}</p>
                {item.returnedAt && <p><strong>Returned At:</strong> {new Date(item.returnedAt).toLocaleDateString()}</p>}

                {!item.returnedAt && (
                  <Button color="danger" onClick={() => handleReturn(item._id)}>
                    Mark as Returned
                  </Button>
                )}
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default BorrowedList;
