import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, NavItem, NavLink, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from "reactstrap";
import { useState, useEffect } from "react";

const Header = () => {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState(null);

  // قراءة الدور عند تحميل المكون
  useEffect(() => {
    const role = localStorage.getItem("userRole");
    if (role) setUserRole(role);
  }, []);

  // دالة Logout
  const handleLogout = () => {
    localStorage.removeItem("userRole"); // مسح الدور
    setUserRole(null); // إخفاء الزر والدائرة
    navigate("/login"); // إعادة التوجيه لصفحة login
  };

  // لا يظهر Header إذا لم يكن هناك مستخدم
  if (!userRole) return null;

  // تحديد لون الدائرة حسب الدور
  const avatarColor = userRole === "admin" ? "#007bff" : "#28a745"; // أزرق Admin، أخضر Student

  return (
    <Navbar color="light" light expand="md" className="mb-4 px-3">
      {/* الروابط الأساسية */}
      <Nav className="me-auto" navbar>
        <NavItem>
          <NavLink tag={Link} to="/">Home</NavLink>
        </NavItem>

        {/* روابط خاصة بالطالب */}
        {userRole === "student" && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/dashboard">Dashboard</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/viewbooks">View Books</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/myborrowed">My Borrowed Books</NavLink>
            </NavItem>
          </>
        )}

        {/* روابط خاصة بالـ Admin */}
        {userRole === "admin" && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/admin">Admin Dashboard</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/book">Books</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/borrowedlist">Borrowed List</NavLink>
            </NavItem>
          </>
        )}
      </Nav>

      {/* أيقونة الدائرة مع Dropdown */}
      <Nav className="ms-auto" navbar className="d-flex align-items-center">
        <UncontrolledDropdown inNavbar>
          <DropdownToggle
            nav
            caret
            style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              backgroundColor: avatarColor,
              color: "#fff",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: "bold",
              marginRight: "10px"
            }}
          >
            {userRole.charAt(0).toUpperCase()}
          </DropdownToggle>
          <DropdownMenu end>
            <DropdownItem header>{userRole.charAt(0).toUpperCase() + userRole.slice(1)}</DropdownItem>
            <DropdownItem divider />
            <DropdownItem onClick={handleLogout}>Logout</DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      </Nav>
    </Navbar>
  );
};

export default Header;
