import { useEffect, useState } from "react";
import axios from "axios";

const BorrowedList = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);

  // -------------------------
  // ✅ ضع الدالة هنا فوق useEffect
  // -------------------------
  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get("http://localhost:3001/borrowedbooks/all");
      setBorrowedBooks(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const handleReturn = async (borrowedId) => {
    try {
      await axios.delete(`http://localhost:3001/borrowedbooks/${borrowedId}`);
      setBorrowedBooks(prev => prev.filter(b => b._id !== borrowedId));
      alert("Book returned successfully!");
    } catch (err) {
      console.error(err);
      alert("Error returning book");
    }
  };

  return (
    <div>
      <h2>Borrowed Books List</h2>

      {borrowedBooks.length === 0 && <p>No borrowed books found.</p>}

      {borrowedBooks.map(item => (
        <div key={item._id}>
          <h4>{item.book?.title}</h4>
          <p>Student: {item.studentUsername}</p>
          <p>Borrowed At: {new Date(item.borrowedAt).toLocaleDateString()}</p>

          <button onClick={() => handleReturn(item._id)}>Return</button>
        </div>
      ))}
    </div>
  );
};

export default BorrowedList;
