import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card } from "reactstrap";
import axios from "axios";

const Dashboard = () => {
  const user = useSelector((state) => state.user.user);
  const [borrowedCount, setBorrowedCount] = useState(0);
  const [availableCount, setAvailableCount] = useState(0);

  useEffect(() => {
    if (user?.username) {
      // عدد الكتب المستعارة للطالب
      axios.get(`http://localhost:3001/borrowedbooks`)
        .then(res => {
          if (Array.isArray(res.data)) {
            const userBorrowed = res.data.filter(b => b.username === user.username);
            setBorrowedCount(userBorrowed.length);
          }
        })
        .catch(err => console.log(err));

      // عدد الكتب الكلي
      axios.get(`http://localhost:3001/books`)
        .then(res => {
          if (Array.isArray(res.data)) {
            setAvailableCount(res.data.length);
          }
        })
        .catch(err => console.log(err));
    }
  }, [user]);

  return (
    <Container className="py-4">
      <Row>
        <Col md={4} className="text-center">
          <img
            src={student}
            alt="user"
            style={{ width: "120px", borderRadius: "50%", marginBottom: "10px" }}
          />
          <h4>{user?.username || "Guest"}</h4>
          <p>Welcome, {user?.username || "Guest"}!</p>
          <Location />
        </Col>

        <Col md={8}>
          <Row>
            <Col>
              <Card body style={{ textAlign: "center", marginBottom: "10px" }}>
                <h5>Borrowed Books</h5>
                <p>{borrowedCount}</p>
              </Card>
            </Col>
            <Col>
              <Card body style={{ textAlign: "center", marginBottom: "10px" }}>
                <h5>Available Books</h5>
                <p>{availableCount}</p>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
