import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllBorrowedBooks } from "../Features/BorrowedBookModelSlice";
import { Container, Input, Card, CardBody } from "reactstrap";

const BorrowedList = () => {
  const dispatch = useDispatch();
  const { borrowedBooks, loading, error } = useSelector((state) => state.borrowed);
  const [search, setSearch] = useState("");

  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  // ðŸ”¹ ÙÙ„ØªØ±Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ø¹ Ø­Ù…Ø§ÙŠØ© Ù…Ù† undefined
  const filtered = borrowedBooks.filter((b) =>
    (b.studentUsername?.toLowerCase().includes(search.toLowerCase())) ||
    (b.book?.title?.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <Container className="py-4">
      <h2 className="mb-3">Borrowed Books List (Admin)</h2>

      <Input
        type="text"
        placeholder="Search by student or book title"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-3"
      />

      {loading && <p>Loading borrowed books...</p>}
      {error && <p className="text-danger">{error}</p>}

      {!loading && filtered.length === 0 && <p>No borrowed books found.</p>}

      {filtered.map((b) => (
        <Card key={b._id} className="mb-2">
          <CardBody>
            <p><strong>Student:</strong> {b.studentUsername}</p>
            <p><strong>Book:</strong> {b.book?.title || "Deleted book"}</p>
            <p><strong>Author:</strong> {b.book?.author || "-"}</p>
            <p><strong>Year:</strong> {b.book?.year || "-"}</p>
            <p><strong>Borrowed At:</strong> {new Date(b.borrowedAt).toLocaleString()}</p>
            <p><strong>Returned At:</strong> {b.returnedAt ? new Date(b.returnedAt).toLocaleString() : "Not returned"}</p>
            <p><strong>Rating:</strong> {b.rating || "-"}</p>
            <p><strong>Feedback:</strong> {b.feedback || "-"}</p>
          </CardBody>
        </Card>
      ))}

      <footer className="mt-4 text-center">
        Â© 2025 Library System. All rights reserved.
      </footer>
    </Container>
  );
};

export default BorrowedList;
