import { Link } from "react-router-dom";
import { Navbar, Nav, NavItem, NavLink } from "reactstrap";

const Header = ({ userRole }) => {
  return (
    <Navbar color="light" light expand="md" className="mb-4">
      <Nav className="me-auto" navbar>
        <NavItem>
          <NavLink tag={Link} to="/">Home</NavLink>
        </NavItem>

        {/* الروابط تظهر فقط بعد تسجيل الدخول حسب الدور */}
        {userRole === "student" && (
          <NavItem>
            <NavLink tag={Link} to="/student-dashboard">Student</NavLink>
          </NavItem>
        )}
        {userRole === "admin" && (
          <NavItem>
            <NavLink tag={Link} to="/admin-dashboard">Admin</NavLink>
          </NavItem>
        )}
      </Nav>

      <Nav navbar>
        {/* إذا ما في دور محدد → نعرض Login / Register */}
        {!userRole && (
          <>
            <NavItem>
              <NavLink tag={Link} to="/login">Login</NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={Link} to="/register">Register</NavLink>
            </NavItem>
          </>
        )}
      </Nav>
    </Navbar>
  );
};

export default Header;
