import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, Row, Col, Card, CardBody, Button, Input } from "reactstrap";
import { fetchBorrowedBooks, returnBook } from "../Features/BorrowedBookModelSlice";

const MyBorrowedBooks = () => {
  const dispatch = useDispatch();
  const username = localStorage.getItem("username");

  const { borrowedBooks, isLoading } = useSelector(state => state.borrowed);

  const [ratings, setRatings] = useState({});
  const [feedbacks, setFeedbacks] = useState({});

  useEffect(() => {
    if (username) dispatch(fetchBorrowedBooks(username));
  }, [dispatch, username]);

  useEffect(() => {
    const initialRatings = {};
    const initialFeedbacks = {};
    borrowedBooks.forEach(b => {
      if (b.book) {
        initialRatings[b.book._id] = b.rating || 0;
        initialFeedbacks[b.book._id] = b.feedback || "";
      }
    });
    setRatings(initialRatings);
    setFeedbacks(initialFeedbacks);
  }, [borrowedBooks]);

  const handleReturn = (borrowedId, bookId) => {
    const rating = ratings[bookId] || 0;
    const feedback = feedbacks[bookId] || "";
    dispatch(returnBook({ borrowedId, rating, feedback }));
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">My Borrowed Books</h3>
      {isLoading && <p>Loading...</p>}
      <Row>
        {borrowedBooks.length === 0 && !isLoading && <p className="text-center">No borrowed books.</p>}
        {borrowedBooks.map(b => {
          if (!b.book) return null;
          const isReturned = !!b.returnedAt;

          return (
            <Col md="4" className="mb-3" key={b._id}>
              <Card className="shadow-sm">
                <CardBody>
                  <h5>{b.book.title}</h5>
                  <p>Author: {b.book.author}</p>
                  <p>Year: {b.book.year}</p>
                  <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                  {b.returnedAt && <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>}

                  {/* Rating */}
                  <div className="mb-2">
                    {[1,2,3,4,5].map(star => (
                      <span
                        key={star}
                        style={{
                          cursor: isReturned ? "not-allowed" : "pointer",
                          color: star <= (ratings[b.book._id] || 0) ? "gold" : "gray",
                          fontSize: "1.2rem",
                          marginRight: "3px"
                        }}
                        onClick={() => !isReturned && setRatings(prev => ({ ...prev, [b.book._id]: star }))}
                      >
                        â˜…
                      </span>
                    ))}
                  </div>

                  <Input
                    type="textarea"
                    placeholder="Leave feedback"
                    value={feedbacks[b.book._id] || ""}
                    disabled={isReturned}
                    onChange={e => !isReturned && setFeedbacks(prev => ({ ...prev, [b.book._id]: e.target.value }))}
                    className="mb-2"
                  />

                  {!isReturned && (
                    <Button color="success" onClick={() => handleReturn(b._id, b.book._id)}>
                      Return Book
                    </Button>
                  )}
                </CardBody>
              </Card>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
