import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchAllBorrowedBooks } from "../Features/BorrowedBookAdminSlice";

const BorrowedList = () => {
  const dispatch = useDispatch();
  const { borrowedBooks, isLoading } = useSelector(state => state.borrowedAdmin);

  useEffect(() => {
    dispatch(fetchAllBorrowedBooks());
  }, [dispatch]);

  if (isLoading) return <p>Loading...</p>;

  return (
    <div>
      <h3>All Borrowed Books</h3>
      {borrowedBooks.length === 0 && <p>No borrowed books found.</p>}
      {borrowedBooks.map(b => (
        <div key={b._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
          <p><strong>User:</strong> {b.studentUsername}</p>
          <p><strong>Book:</strong> {b.book?.title}</p>
          <p><strong>Author:</strong> {b.book?.author}</p>
          <p><strong>Borrowed At:</strong> {new Date(b.borrowedAt).toLocaleDateString()}</p>
          <p><strong>Returned At:</strong> {b.returnedAt ? new Date(b.returnedAt).toLocaleDateString() : "Not returned"}</p>
          <p><strong>Rating:</strong> {b.rating}</p>
          <p><strong>Feedback:</strong> {b.feedback}</p>
        </div>
      ))}
    </div>
  );
};

export default BorrowedList;
