import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card, CardBody } from "reactstrap";
import { fetchBorrowedBooks } from "../store/BorrowedBookModelSlice"; // عدّل المسار لو لازم
// إذا عندك slice للكتب العامة (available books)
import { fetchBooks } from "../store/BookSlice"; // اختياري — عدّل المسار لو لازم

const Dashboard = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user);

  // borrowed items array from your borrowed slice
  const borrowedItems = useSelector((state) => state.borrowed?.items || []);
  // books list from books slice (assumes each book has e.g. available: true/false or quantity)
  const books = useSelector((state) => state.books?.items || []);

  // Compute counts
  const borrowedCount = borrowedItems.length;
  // Adjust this predicate to match your book model (e.g. book.available === true)
  const availableCount = books.filter((b) => b.available === true || b.quantity > 0).length;

  // Fetch data when user is present
  useEffect(() => {
    if (user?.username) {
      // Make sure you pass the correct username field that backend expects
      dispatch(fetchBorrowedBooks(user.username));
      // optional: fetch all books if you want available count here
      dispatch(fetchBooks());
    }
  }, [user, dispatch]);

  return (
    <Container className="py-4">
      <Row className="align-items-start">
        {/* Left column: image + Location stacked */}
        <Col xs="12" md="4" className="d-flex flex-column align-items-start">
          <img
            src={student}
            alt="user"
            style={{
              width: "120px",
              borderRadius: "50%",
              marginBottom: "12px",
            }}
          />
          {/* Location under the image */}
          <div style={{ width: "100%", marginTop: "6px" }}>
            <Location />
          </div>
        </Col>

        {/* Right column: greeting and stats */}
        <Col xs="12" md="8">
          <Card className="mb-3">
            <CardBody>
              {/* English welcome */}
              <h3>Welcome, {user?.fullName || "Guest"}!</h3>
              <p style={{ marginTop: "6px" }}>
                Here is your library summary:
              </p>

              <div style={{ display: "flex", gap: "20px", marginTop: "12px" }}>
                <div>
                  <h5 style={{ marginBottom: "4px" }}>{borrowedCount}</h5>
                  <small>Books borrowed</small>
                </div>

                <div>
                  <h5 style={{ marginBottom: "4px" }}>{availableCount}</h5>
                  <small>Books available</small>
                </div>
              </div>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
