import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";

const Login = ({ setRole }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [roleSelected, setRoleSelected] = useState("student");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // ارسال بيانات الدخول للـ backend
      const res = await axios.post("http://localhost:5000/login", { username, password });
      const userRole = roleSelected; // هنا نستخدم الدور المحدد من الـ radio
      setRole(userRole); // تحديث state الدور في App
      if(userRole === "admin") {
        navigate("/admin");
      } else {
        navigate("/dashboard");
      }
    } catch (err) {
      alert(err.response.data.error || "Login failed");
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <Row className="w-100 justify-content-center">
        <Col md="4">
          <Card className="shadow-lg p-3">
            <CardBody>
              <h3 className="text-center mb-4">Login</h3>

              <Form>
                <FormGroup>
                  <Label>Username</Label>
                  <Input type="text" placeholder="Enter username" value={username} onChange={e => setUsername(e.target.value)} required />
                </FormGroup>

                <FormGroup>
                  <Label>Password</Label>
                  <Input type="password" placeholder="Enter password" value={password} onChange={e => setPassword(e.target.value)} required />
                </FormGroup>

                <FormGroup className="d-flex justify-content-between">
                  <div>
                    <Input type="radio" name="role" checked={roleSelected === "student"} onChange={()=>setRoleSelected("student")} /> Student
                  </div>
                  <div>
                    <Input type="radio" name="role" checked={roleSelected === "admin"} onChange={()=>setRoleSelected("admin")} /> Admin
                  </div>
                </FormGroup>

                <Button color="primary" className="w-100 mt-3" type="button" onClick={handleLogin}>
                  Login
                </Button>

                <div className="text-center mt-3">
                  <span>Don't have an account? </span>
                  <Link to="/register">Register</Link>
                </div>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
