import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const SERVER_URL = "http://localhost:3001";

export const fetchAllBorrowedBooks = createAsyncThunk(
  "borrowedAdmin/fetchAll",
  async (_, { rejectWithValue }) => {
    try {
      const res = await axios.get(`${SERVER_URL}/borrowedbooks/all`);
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || "Error fetching borrowed books");
    }
  }
);

const initialState = {
  borrowedBooks: [],
  isLoading: false,
  isError: false,
  errorMessage: "",
};

const BorrowedBookAdminSlice = createSlice({
  name: "borrowedAdmin",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchAllBorrowedBooks.pending, (state) => { state.isLoading = true; })
      .addCase(fetchAllBorrowedBooks.fulfilled, (state, action) => {
        state.isLoading = false;
        state.borrowedBooks = action.payload;
      })
      .addCase(fetchAllBorrowedBooks.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.errorMessage = action.payload;
      });
  }
});

export default BorrowedBookAdminSlice.reducer;
