import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody } from "reactstrap";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const username = localStorage.getItem("username"); // نفس الاسم عند login

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/myborrowed/${username}`);
      setBorrowedBooks(res.data);
    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">My Borrowed Books</h3>

      <Row>
        {borrowedBooks.length === 0 && <p className="text-center">No books borrowed yet.</p>}
        {borrowedBooks.map((b) => (
          <Col md="4" className="mb-3" key={b._id}>
            <Card className="shadow-sm">
              <CardBody>
                <h5>{b.title}</h5>
                <p>Author: {b.author}</p>
                <p>Year: {b.year}</p>
                <p>Borrowed At: {new Date(b.borrowedAt).toLocaleString()}</p>
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
