// src/Component/MyBorrowedBooks.js
import { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, CardBody, Button, Input } from "reactstrap";

const MyBorrowedBooks = () => {
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [ratings, setRatings] = useState({});
  const [feedbacks, setFeedbacks] = useState({});
  const username = localStorage.getItem("username");

  useEffect(() => {
    fetchBorrowedBooks();
  }, []);

  const fetchBorrowedBooks = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/borrowedbooks/${username}`);
      setBorrowedBooks(res.data);

      const initialRatings = {};
      const initialFeedbacks = {};
      res.data.forEach(b => {
        const bookId = b.book?._id;
        if (bookId) {
          initialRatings[bookId] = b.rating || 0;
          initialFeedbacks[bookId] = b.feedback || "";
        }
      });
      setRatings(initialRatings);
      setFeedbacks(initialFeedbacks);

    } catch (err) {
      console.error("Error fetching borrowed books:", err);
    }
  };

  const handleStarClick = (bookId, star, isReturned) => {
    if (!isReturned && bookId) {
      setRatings(prev => ({ ...prev, [bookId]: star }));
    }
  };

  const handleFeedbackChange = (bookId, value, isReturned) => {
    if (!isReturned && bookId) {
      setFeedbacks(prev => ({ ...prev, [bookId]: value }));
    }
  };

  const handleReturn = async (borrowedId, bookId) => {
    try {
      const rating = ratings[bookId] || 0;
      const feedback = feedbacks[bookId] || "";

      await axios.put(`http://localhost:3001/borrowedbooks/return/${borrowedId}`, {
        rating,
        feedback
      });

      // Remove returned book from list
      setBorrowedBooks(prev => prev.filter(b => b._id !== borrowedId));

      alert("Book returned successfully!");
    } catch (err) {
      console.error(err);
      alert("Error returning book");
    }
  };

  return (
    <Container className="py-4">
      <h3 className="mb-4 text-center">My Borrowed Books</h3>
      <Row>
        {borrowedBooks.length === 0 && <p className="text-center">No borrowed books.</p>}

        {borrowedBooks.map(b => {
          const book = b.book;
          const isReturned = !!b.returnedAt;

          return (
            <Col md="4" className="mb-3" key={b._id}>
              <Card className="shadow-sm">
                <CardBody>
                  <h5>{book?.title || "Book not found"}</h5>
                  <p>Author: {book?.author || "N/A"}</p>
                  <p>Year: {book?.year || "N/A"}</p>
                  <p>Borrowed At: {new Date(b.borrowedAt).toLocaleDateString()}</p>
                  {isReturned && <p>Returned At: {new Date(b.returnedAt).toLocaleDateString()}</p>}

                  {/* Star Rating */}
                  <div className="mb-2">
                    {[1,2,3,4,5].map(star => (
                      <span
                        key={star}
                        style={{
                          cursor: isReturned || !book ? "not-allowed" : "pointer",
                          color: star <= (ratings[book?._id] || 0) ? "gold" : "gray",
                          fontSize: "1.2rem",
                          marginRight: "3px"
                        }}
                        onClick={() => handleStarClick(book?._id, star, isReturned)}
                      >
                        â˜…
                      </span>
                    ))}
                  </div>

                  <Input
                    type="textarea"
                    placeholder="Leave feedback"
                    value={feedbacks[book?._id] || ""}
                    onChange={(e) => handleFeedbackChange(book?._id, e.target.value, isReturned)}
                    className="mb-2"
                    disabled={isReturned || !book}
                  />

                  {!isReturned && book && (
                    <Button
                      color="success"
                      className="w-100"
                      onClick={() => handleReturn(b._id, book._id)}
                    >
                      Return & Rate
                    </Button>
                  )}
                </CardBody>
              </Card>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export default MyBorrowedBooks;
