import React, { useState, useEffect } from "react";

const Location = () => {
  const OPENCAGE_API_KEY = "74df35ab5a34481da6160b38488ce3af";
  const [location, setLocation] = useState(null);
  const [placeInfo, setPlaceInfo] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!("geolocation" in navigator)) {
      setError("Geolocation not supported by your browser");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude, accuracy } = position.coords;
        setLocation({ latitude, longitude, accuracy });

        try {
          const response = await fetch(
            `https://api.opencagedata.com/geocode/v1/json?q=${latitude}+${longitude}&key=${OPENCAGE_API_KEY}`
          );
          const data = await response.json();

          if (data?.results?.length > 0) {
            const components = data.results[0].components;
            setPlaceInfo({
              city: components.city || components.town || components.village || "Unknown",
              region: components.state || "Unknown",
              country: components.country || "Unknown",
            });
          } else {
            setError("Could not retrieve location details.");
          }
        } catch {
          setError("Failed to fetch reverse geolocation.");
        }
      },
      () => {
        setError("Permission denied or location unavailable");
      }
    );
  }, []);

  return (
    <div style={{ marginTop: "1rem" }}>
      {error && <p>{error}</p>}
      {location && (
        <p>
          Lat: {location.latitude}, Lng: {location.longitude}, Accuracy: {location.accuracy} m
        </p>
      )}
      {placeInfo && (
        <p>
          {placeInfo.city}, {placeInfo.region}, {placeInfo.country}
        </p>
      )}
      {!location && !error && <p>Getting location...</p>}
    </div>
  );
};

export default Location;
