import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import student from "../images/student.png";
import Location from "./Location";
import { Container, Row, Col, Card, CardBody } from "reactstrap";
import { fetchBooks } from "../Features/BookSlice";
import { fetchBorrowedBooks } from "../Features/BorrowedSlice";

const Dashboard = () => {
  const dispatch = useDispatch();

  const user = useSelector((state) => state.user.user);
  const books = useSelector((state) => state.books.books);
  const borrowed = useSelector((state) => state.borrowed.items);

  useEffect(() => {
    dispatch(fetchBooks());
    if (user?.username) {
      dispatch(fetchBorrowedBooks(user.username));
    }
  }, [dispatch, user?.username]);

  return (
    <Container className="py-4">
      <Row>
        <Col md="4" style={{ textAlign: "center" }}>
          <img
            src={student}
            alt="user"
            style={{ width: "120px", borderRadius: "50%", marginBottom: "20px" }}
          />
          <h4>Welcome, {user?.fullName || "Guest"}!</h4>
          <div className="mt-3">
            <Location />
          </div>
        </Col>
        <Col md="8">
          <Row>
            <Col md="6">
              <Card className="mb-3">
                <CardBody style={{ textAlign: "center" }}>
                  <h5>Total Books</h5>
                  <h2>{books.length}</h2>
                </CardBody>
              </Card>
            </Col>
            <Col md="6">
              <Card className="mb-3">
                <CardBody style={{ textAlign: "center" }}>
                  <h5>Borrowed Books</h5>
                  <h2>{borrowed.length}</h2>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
